import React, {useState, useEffect, useRef} from "react";
import "./Charts.css";
import {Line} from "react-chartjs-2";
import {chartData} from "./Tickers.js";

const options = {
    legend: {
        display: false,
    },
    hover: {
        intersect: false
    },
    elements: {
        line: {
            tension: 0
        },
        point: {
            radius: 0,
        },
    },
    maintainAspectRatio: true,
    tooltips: {
        mode: "index",
        intersect: false,
        callbacks: {
        },
    },
    scales: {
        xAxes: [
            {
                type: "time",
                time: {
                    format: "MM/DD/YY",
                    tooltipFormat: "ll",
                },
                ticks: {
                    display: true,
                }
            },
        ],
        yAxes: [
            {
                gridLines: {
                    display: false,
                },
                ticks: {
                    display: true,
                },
            },
        ],
    },
};


function Charts() {

    const [graphData, setData] = useState([]);

    useEffect(() => {

        let data = [];
        let date = new Date();
        graphData.push({x: date, y: chartData.get("GTBY")[chartData.get("GTBY").length - 1]});
        setData(data)
        console.log("size = " + graphData.length);
    });

    return (
        <div className="charts_wrapper">
            <Line data={{
                //labels: ['January', 'February', 'March', 'April', 'May', 'June'],
                datasets: [
                    {
                        type: 'line',
                        backgroundColor: "black",
                        borderColor: "#5AC53B",
                        borderWidth: 2,
                        pointBorderColor: 'rgba(0, 0, 0, 0)',
                        pointBackgroundColor: 'rgba(0, 0, 0, 0)',
                        pointHoverBackgroundColor: '#5AC53B',
                        pointHoverBorderColor: '#000000',
                        pointHoverBorderWidth: 4,
                        pointHoverRadius: 6,
                        //data: chartData.get("GTBY"),
                        data: graphData
                    },
                ],
            }}
          options={options}
            />
        </div>
    )
}






export default Charts
