import React, {useState, useEffect} from "react";
import "./Charts.css";
import {Line} from "react-chartjs-2";
import {chartData} from "./Tickers.js";

const options = {
    legend: {
        display: false,
    },
    hover: {
        intersect: false
    },
    elements: {
        line: {
            tension: 0
        },
        point: {
            radius: 0,
        },
    },
    maintainAspectRatio: false,
    tooltips: {
        mode: "index",
        intersect: false,
        callbacks: {
        },
    },
    scales: {
        xAxes: [
            {
                gridLines: {
                    display: true,
                },
                ticks: {
                    display: true,

                },
            },
        ],
        yAxes: [
            {
                gridLines: {
                    display: true,
                },
                ticks: {
                    display: true,
                },
            },
        ],
    },
};


function Charts() {

    const [data, setData] = useState([]);
    const [lab, setLab] = useState([]);
    var x = 1;

    useEffect(() => {

        let data = [];
        let l = [];
        //let date = new Date();
        x=x+1;
        //data.push({x: x, y: chartData.get("GTBY")[chartData.get("GTBY").length - 1]});
        data.push(chartData.get("GTBY")[chartData.get("GTBY").length - 1]);
        setData(data);
        l.push(x)
        setLab(l);


    });

    return (
        <div className="charts_wrapper">
            <Line data={{
                labels: lab,
                datasets: [
                    {
                        type: 'line',
                        backgroundColor: "black",
                        borderColor: "#5AC53B",
                        borderWidth: 2,
                        pointBorderColor: 'rgba(0, 0, 0, 0)',
                        pointBackgroundColor: 'rgba(0, 0, 0, 0)',
                        pointHoverBackgroundColor: '#5AC53B',
                        pointHoverBorderColor: '#000000',
                        pointHoverBorderWidth: 4,
                        pointHoverRadius: 6,
                        data: data
                    },
                ],
            }}
          options={options}
            />
        </div>
    )
}






export default Charts
